  maxBatchSize: parseInt(process.env.BATCH_MAX_SIZE || "100"),
  maxBatchAgeMs: parseInt(process.env.BATCH_MAX_AGE_MS || "300000"),
  enabled: process.env.BATCH_ENABLED !== "false",
});

