import { describe, it, expect, beforeEach, afterEach, vi } from "vitest";
import {
  MerkleTree,
  BatchAnchoringService,
  BatchableEvent,
  BatchConfig,
} from "../../server/batch-anchoring";

describe("MerkleTree", () => {
  describe("constructor and tree building", () => {
    it("should create a tree with single leaf", () => {
      const tree = new MerkleTree(["data1"]);
      const root = tree.getRoot();

      expect(root).toBeDefined();
      expect(root).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should create a tree with multiple leaves", () => {
      const tree = new MerkleTree(["data1", "data2", "data3", "data4"]);
      const root = tree.getRoot();

      expect(root).toBeDefined();
      expect(root).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should create a tree with odd number of leaves", () => {
      const tree = new MerkleTree(["data1", "data2", "data3"]);
      const root = tree.getRoot();

      expect(root).toBeDefined();
      expect(root).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should handle empty data array", () => {
      const tree = new MerkleTree([]);
      const root = tree.getRoot();

      expect(root).toBeDefined();
      expect(root).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should produce consistent roots for same data", () => {
      const data = ["event1", "event2", "event3"];
      const tree1 = new MerkleTree(data);
      const tree2 = new MerkleTree(data);

      expect(tree1.getRoot()).toBe(tree2.getRoot());
    });

    it("should produce different roots for different data", () => {
      const tree1 = new MerkleTree(["data1", "data2"]);
      const tree2 = new MerkleTree(["data1", "data3"]);

      expect(tree1.getRoot()).not.toBe(tree2.getRoot());
    });
  });

  describe("getProof", () => {
    it("should generate valid proof for first leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);

      expect(proof).toBeInstanceOf(Array);
      expect(proof.length).toBeGreaterThan(0);
    });

    it("should generate valid proof for last leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(3);

      expect(proof).toBeInstanceOf(Array);
      expect(proof.length).toBeGreaterThan(0);
    });

    it("should generate valid proof for middle leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(2);

      expect(proof).toBeInstanceOf(Array);
    });

    it("should generate correct proof length for power-of-2 trees", () => {
      const data = ["a", "b", "c", "d", "e", "f", "g", "h"]; // 8 leaves = 3 levels
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);

      expect(proof.length).toBe(3); // log2(8) = 3
    });
  });

  describe("verify", () => {
    it("should verify valid proof", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);

      const isValid = MerkleTree.verify(leafHash, proof, root);
      expect(isValid).toBe(true);
    });

    it("should verify valid proof for any index", () => {
      const data = ["a", "b", "c", "d"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();

      for (let i = 0; i < data.length; i++) {
        const proof = tree.getProof(i);
        const leafHash = MerkleTree.hashEventData(data[i]);
        expect(MerkleTree.verify(leafHash, proof, root)).toBe(true);
      }
    });

    it("should reject invalid proof with wrong leaf", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const wrongLeafHash = MerkleTree.hashEventData("wrong_event");

      const isValid = MerkleTree.verify(wrongLeafHash, proof, root);
      expect(isValid).toBe(false);
    });

    it("should reject invalid proof with wrong root", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);
      const wrongRoot = "0x" + "0".repeat(64);

      const isValid = MerkleTree.verify(leafHash, proof, wrongRoot);
      expect(isValid).toBe(false);
    });

    it("should reject proof with missing sibling", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);

      const incompleteProof = proof.slice(0, -1);
      const isValid = MerkleTree.verify(leafHash, incompleteProof, root);
      expect(isValid).toBe(false);
    });
  });

  describe("hashEventData", () => {
    it("should hash string data consistently", () => {
      const data = "test event data";
      const hash1 = MerkleTree.hashEventData(data);
      const hash2 = MerkleTree.hashEventData(data);

      expect(hash1).toBe(hash2);
    });

    it("should produce different hashes for different data", () => {
      const hash1 = MerkleTree.hashEventData("data1");
      const hash2 = MerkleTree.hashEventData("data2");

      expect(hash1).not.toBe(hash2);
    });

    it("should produce valid keccak256 format", () => {
      const hash = MerkleTree.hashEventData("test");

      expect(hash).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });
  });
});

