      const tree1 = new MerkleTree(data);
      const tree2 = new MerkleTree(data);

      expect(tree1.getRoot()).toBe(tree2.getRoot());
    });

    it("should produce different roots for different data", () => {
      const tree1 = new MerkleTree(["data1", "data2"]);
      const tree2 = new MerkleTree(["data1", "data3"]);

      expect(tree1.getRoot()).not.toBe(tree2.getRoot());
    });
  });

  describe("getProof", () => {
    it("should generate valid proof for first leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);

      expect(proof).toBeInstanceOf(Array);
      expect(proof.length).toBeGreaterThan(0);
    });

    it("should generate valid proof for last leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(3);

      expect(proof).toBeInstanceOf(Array);
      expect(proof.length).toBeGreaterThan(0);
    });

    it("should generate valid proof for middle leaf", () => {
      const data = ["data1", "data2", "data3", "data4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(2);

      expect(proof).toBeInstanceOf(Array);
    });

    it("should generate correct proof length for power-of-2 trees", () => {
      const data = ["a", "b", "c", "d", "e", "f", "g", "h"]; // 8 leaves = 3 levels
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);

      expect(proof.length).toBe(3); // log2(8) = 3
    });
  });

  describe("verify", () => {
    it("should verify valid proof", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);

      const isValid = MerkleTree.verify(leafHash, proof, root);
      expect(isValid).toBe(true);
    });

    it("should verify valid proof for any index", () => {
      const data = ["a", "b", "c", "d"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();

      for (let i = 0; i < data.length; i++) {
        const proof = tree.getProof(i);
        const leafHash = MerkleTree.hashEventData(data[i]);
        expect(MerkleTree.verify(leafHash, proof, root)).toBe(true);
      }
    });

    it("should reject invalid proof with wrong leaf", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const wrongLeafHash = MerkleTree.hashEventData("wrong_event");

      const isValid = MerkleTree.verify(wrongLeafHash, proof, root);
      expect(isValid).toBe(false);
    });

    it("should reject invalid proof with wrong root", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);
      const wrongRoot = "0x" + "0".repeat(64);

      const isValid = MerkleTree.verify(leafHash, proof, wrongRoot);
      expect(isValid).toBe(false);
    });

    it("should reject proof with missing sibling", () => {
      const data = ["event1", "event2", "event3", "event4"];
      const tree = new MerkleTree(data);
      const root = tree.getRoot();
      const proof = tree.getProof(0);
      const leafHash = MerkleTree.hashEventData(data[0]);

      const incompleteProof = proof.slice(0, -1);
      const isValid = MerkleTree.verify(leafHash, incompleteProof, root);
      expect(isValid).toBe(false);
    });
  });

  describe("hashEventData", () => {
    it("should hash string data consistently", () => {
      const data = "test event data";
      const hash1 = MerkleTree.hashEventData(data);
      const hash2 = MerkleTree.hashEventData(data);

      expect(hash1).toBe(hash2);
    });

    it("should produce different hashes for different data", () => {
      const hash1 = MerkleTree.hashEventData("data1");
      const hash2 = MerkleTree.hashEventData("data2");

      expect(hash1).not.toBe(hash2);
    });

    it("should produce valid keccak256 format", () => {
      const hash = MerkleTree.hashEventData("test");

      expect(hash).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });
  });
});

describe("BatchAnchoringService", () => {
  let service: BatchAnchoringService;

  const createMockEvent = (id: string): BatchableEvent => ({
    id,
    assetId: `asset-${id}`,
    eventType: "telemetry",
    payload: { value: Math.random() * 100 },
    timestamp: new Date(),
  });

  beforeEach(() => {
    vi.useFakeTimers();
    service = new BatchAnchoringService({
      maxBatchSize: 5,
      maxBatchAgeMs: 60000,
      enabled: true,
    });
  });

  afterEach(() => {
    service.shutdown();
    vi.useRealTimers();
  });

  describe("constructor", () => {
    it("should initialize with default config", () => {
      const defaultService = new BatchAnchoringService();
      const config = defaultService.getConfig();

      expect(config.maxBatchSize).toBe(100);
      expect(config.maxBatchAgeMs).toBe(300000);
      expect(config.enabled).toBe(true);

      defaultService.shutdown();
    });

    it("should initialize with custom config", () => {
      const config = service.getConfig();

      expect(config.maxBatchSize).toBe(5);
      expect(config.maxBatchAgeMs).toBe(60000);
      expect(config.enabled).toBe(true);
    });

    it("should initialize stats correctly", () => {
      const stats = service.getStats();

      expect(stats.pendingEvents).toBe(0);
      expect(stats.totalBatchesAnchored).toBe(0);
      expect(stats.totalEventsAnchored).toBe(0);
      expect(stats.lastBatchTime).toBeNull();
      expect(stats.averageEventsPerBatch).toBe(0);
      expect(stats.estimatedGasSavings).toBe(0);
    });
  });

  describe("queueEvent", () => {
    it("should queue event when enabled", () => {
      const event = createMockEvent("1");
      const result = service.queueEvent(event);

      expect(result.queued).toBe(true);
      expect(result.position).toBe(1);
    });

    it("should not queue event when disabled", () => {
      service.updateConfig({ enabled: false });
      const event = createMockEvent("1");
      const result = service.queueEvent(event);

      expect(result.queued).toBe(false);
      expect(result.position).toBe(-1);
    });

    it("should track pending events count", () => {
      service.queueEvent(createMockEvent("1"));
      service.queueEvent(createMockEvent("2"));
      service.queueEvent(createMockEvent("3"));

      const stats = service.getStats();
      expect(stats.pendingEvents).toBe(3);
    });

    it("should auto-flush when max batch size reached", async () => {
      for (let i = 1; i <= 5; i++) {
        service.queueEvent(createMockEvent(String(i)));
      }

      // Give async operations time to complete
      await vi.runAllTimersAsync();

      const stats = service.getStats();
      expect(stats.totalBatchesAnchored).toBe(1);
      expect(stats.totalEventsAnchored).toBe(5);
      expect(stats.pendingEvents).toBe(0);
    });
  });

  describe("getPendingEvents", () => {
    it("should return copy of pending events", () => {
      const event1 = createMockEvent("1");
      const event2 = createMockEvent("2");

      service.queueEvent(event1);
      service.queueEvent(event2);

      const pending = service.getPendingEvents();
      expect(pending).toHaveLength(2);
      expect(pending[0].id).toBe("1");
      expect(pending[1].id).toBe("2");
    });

    it("should not allow modification of internal queue", () => {
      service.queueEvent(createMockEvent("1"));

      const pending = service.getPendingEvents();
      pending.push(createMockEvent("2"));

      expect(service.getPendingEvents()).toHaveLength(1);
    });
  });

  describe("forceBatch", () => {
    it("should manually flush batch", async () => {
      service.queueEvent(createMockEvent("1"));
      service.queueEvent(createMockEvent("2"));

      const result = await service.forceBatch();

      expect(result).not.toBeNull();
      expect(result!.eventCount).toBe(2);
      expect(result!.merkleRoot).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should return null for empty queue", async () => {
      const result = await service.forceBatch();
      expect(result).toBeNull();
    });

    it("should include batch in history", async () => {
      service.queueEvent(createMockEvent("1"));
      await service.forceBatch();

      const history = service.getBatchHistory();
      expect(history).toHaveLength(1);
    });
  });

  describe("updateConfig", () => {
    it("should update max batch size", () => {
      service.updateConfig({ maxBatchSize: 10 });
      expect(service.getConfig().maxBatchSize).toBe(10);
    });

    it("should update enabled state", () => {
      service.updateConfig({ enabled: false });
      expect(service.getConfig().enabled).toBe(false);
    });

    it("should preserve unmodified config values", () => {
      service.updateConfig({ maxBatchSize: 10 });
      const config = service.getConfig();

      expect(config.maxBatchSize).toBe(10);
      expect(config.maxBatchAgeMs).toBe(60000);
      expect(config.enabled).toBe(true);
    });
  });

  describe("getProofForEvent", () => {
    it("should return proof for event in batch", async () => {
      const events = [
        createMockEvent("1"),
        createMockEvent("2"),
        createMockEvent("3"),
      ];

      events.forEach(e => service.queueEvent(e));
      const batch = await service.forceBatch();

      const proofData = service.getProofForEvent(batch!.batchId, "2");

      expect(proofData).not.toBeNull();
      expect(proofData!.proof).toBeInstanceOf(Array);
      expect(proofData!.root).toBe(batch!.merkleRoot);
      expect(proofData!.eventHash).toMatch(/^0x[a-fA-F0-9]{64}$/);
    });

    it("should return null for non-existent batch", () => {
      const result = service.getProofForEvent("non-existent", "1");
      expect(result).toBeNull();
    });

    it("should return null for non-existent event in batch", async () => {
      service.queueEvent(createMockEvent("1"));
      const batch = await service.forceBatch();

      const result = service.getProofForEvent(batch!.batchId, "non-existent");
      expect(result).toBeNull();
    });

    it("should generate verifiable proof", async () => {
      const events = [
        createMockEvent("1"),
        createMockEvent("2"),
        createMockEvent("3"),
        createMockEvent("4"),
      ];

      events.forEach(e => service.queueEvent(e));
      const batch = await service.forceBatch();

      const proofData = service.getProofForEvent(batch!.batchId, "2");

      const isValid = MerkleTree.verify(
        proofData!.eventHash,
        proofData!.proof,
        proofData!.root
      );

      expect(isValid).toBe(true);
    });
  });

  describe("stats tracking", () => {
    it("should update stats after batch", async () => {
      service.queueEvent(createMockEvent("1"));
      service.queueEvent(createMockEvent("2"));
      await service.forceBatch();

      const stats = service.getStats();

      expect(stats.totalBatchesAnchored).toBe(1);
      expect(stats.totalEventsAnchored).toBe(2);
      expect(stats.averageEventsPerBatch).toBe(2);
      expect(stats.lastBatchTime).not.toBeNull();
    });

    it("should calculate gas savings estimate", async () => {
      for (let i = 0; i < 3; i++) {
        service.queueEvent(createMockEvent(String(i)));
      }
      await service.forceBatch();

      const stats = service.getStats();
      // 3 events * 50000 individual gas - 70000 batch gas = 80000 savings
      expect(stats.estimatedGasSavings).toBe(80000);
    });

    it("should accumulate stats across multiple batches", async () => {
      service.queueEvent(createMockEvent("1"));
      service.queueEvent(createMockEvent("2"));
      await service.forceBatch();

      service.queueEvent(createMockEvent("3"));
      service.queueEvent(createMockEvent("4"));
      service.queueEvent(createMockEvent("5"));
      await service.forceBatch();

      const stats = service.getStats();

      expect(stats.totalBatchesAnchored).toBe(2);
      expect(stats.totalEventsAnchored).toBe(5);
      expect(stats.averageEventsPerBatch).toBe(2.5);
    });
  });

  describe("batch history", () => {
    it("should maintain batch history", async () => {
      service.queueEvent(createMockEvent("1"));
      await service.forceBatch();
      service.queueEvent(createMockEvent("2"));
      await service.forceBatch();

      const history = service.getBatchHistory();
      expect(history).toHaveLength(2);
    });

    it("should limit history to 100 batches", async () => {
      // Queue and flush 105 batches
      for (let i = 0; i < 105; i++) {
        service.queueEvent(createMockEvent(String(i)));
        await service.forceBatch();
      }

      const history = service.getBatchHistory();
      expect(history).toHaveLength(100);
    });

    it("should return copy of history", async () => {
      service.queueEvent(createMockEvent("1"));
      await service.forceBatch();

      const history = service.getBatchHistory();
      history.push({} as any);

      expect(service.getBatchHistory()).toHaveLength(1);
    });
  });

  describe("timer-based flushing", () => {
    it("should flush on timer expiry", async () => {
      service.queueEvent(createMockEvent("1"));

      // Advance timer past max age
      await vi.advanceTimersByTimeAsync(60001);

      const stats = service.getStats();
      expect(stats.totalBatchesAnchored).toBe(1);
    });

    it("should not flush empty queue on timer", async () => {
      await vi.advanceTimersByTimeAsync(60001);

      const stats = service.getStats();
      expect(stats.totalBatchesAnchored).toBe(0);
    });
  });

  describe("shutdown", () => {
    it("should flush pending events on shutdown", async () => {
      service.queueEvent(createMockEvent("1"));
      service.queueEvent(createMockEvent("2"));

      service.shutdown();

      // Note: shutdown calls flushBatch synchronously
      const stats = service.getStats();
      expect(stats.pendingEvents).toBe(0);
    });
  });
});

