contracts/ChangeIntent.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./SiteRegistry.sol";

/**
 * @title ChangeIntent
 * @notice PRD Section 7.3: Change Intent Contract
 * 
 * Anchors:
 * - Blueprint hashes
 * - Codegen output hashes
 * - Approval signatures
 * 
 * Finality before deployment - changes must be anchored before they can be deployed.
 */
contract ChangeIntent {

    SiteRegistry public immutable siteRegistry;

    enum IntentStatus {
        PENDING,
        APPROVED,
        REJECTED,
        DEPLOYED,
        ROLLED_BACK
    }

    struct Intent {
        bytes32 siteId;
        bytes32 blueprintHash;
        bytes32 codeHash;
        bytes32 changePackageHash;
        uint256 requiredApprovals;
        uint256 approvalCount;
        IntentStatus status;
        uint256 createdAt;
        uint256 approvedAt;
        uint256 deployedAt;
        address createdBy;
        address deployedBy;
    }

    struct Approval {
        address approver;
        bytes32 signatureHash;  // Hash of off-chain signature
        string comment;
        uint256 approvedAt;
    }

    // Intent ID => Intent
    mapping(bytes32 => Intent) public intents;

    // Intent ID => approver => Approval
    mapping(bytes32 => mapping(address => Approval)) public approvals;

    // Intent ID => list of approvers
    mapping(bytes32 => address[]) public intentApprovers;

    // Site ID => list of intent IDs
    mapping(bytes32 => bytes32[]) public siteIntents;

    // Track all intent IDs
    bytes32[] public intentIds;

    // Events
    event IntentCreated(
        bytes32 indexed intentId,
        bytes32 indexed siteId,
        bytes32 blueprintHash,
        bytes32 codeHash,
        uint256 requiredApprovals,
        address indexed createdBy,
        uint256 timestamp
    );

    event IntentApproved(
        bytes32 indexed intentId,
        address indexed approver,
        uint256 approvalCount,
        uint256 timestamp
    );

    event IntentRejected(
        bytes32 indexed intentId,
        address indexed rejectedBy,
        string reason,
        uint256 timestamp
    );

    event IntentDeployed(
        bytes32 indexed intentId,
        address indexed deployedBy,
        uint256 timestamp
    );

    event IntentRolledBack(
        bytes32 indexed intentId,
        address indexed rolledBackBy,
        string reason,
        uint256 timestamp
    );

    constructor(address _siteRegistry) {
        siteRegistry = SiteRegistry(_siteRegistry);
    }

    /**
     * @notice Create a new change intent
     * @param siteId The site this change is for
     * @param blueprintHash Hash of the blueprint being changed
     * @param codeHash Hash of the generated code
     * @param changePackageHash Hash of the change package (diff, test plan, rollback)
     * @param requiredApprovals Number of approvals needed
     */
    function createIntent(
        bytes32 siteId,
        bytes32 blueprintHash,
        bytes32 codeHash,
        bytes32 changePackageHash,
        uint256 requiredApprovals
    ) external returns (bytes32 intentId) {
        // Verify caller is authorized
        require(
            siteRegistry.isSignerAuthorized(siteId, msg.sender),
            "Not authorized to create intents for this site"
        );

        require(blueprintHash != bytes32(0), "Invalid blueprint hash");
        require(requiredApprovals > 0, "Must require at least one approval");

        // Generate unique intent ID
        intentId = keccak256(abi.encodePacked(
            siteId,
            blueprintHash,
            codeHash,
            block.timestamp,
            msg.sender
        ));

        require(intents[intentId].createdAt == 0, "Intent already exists");

        intents[intentId] = Intent({
            siteId: siteId,
            blueprintHash: blueprintHash,
            codeHash: codeHash,
            changePackageHash: changePackageHash,
            requiredApprovals: requiredApprovals,
            approvalCount: 0,
            status: IntentStatus.PENDING,
            createdAt: block.timestamp,
            approvedAt: 0,
            deployedAt: 0,
            createdBy: msg.sender,
            deployedBy: address(0)
        });

        intentIds.push(intentId);
        siteIntents[siteId].push(intentId);

        emit IntentCreated(
            intentId,
            siteId,
            blueprintHash,
            codeHash,
            requiredApprovals,
            msg.sender,
            block.timestamp
        );

        return intentId;
    }

    /**
     * @notice Approve a change intent
     * @param intentId The intent to approve
     * @param signatureHash Hash of the approver's off-chain signature
     * @param comment Optional comment
     */
    function approveIntent(
        bytes32 intentId,
        bytes32 signatureHash,
        string calldata comment
    ) external {
        Intent storage intent = intents[intentId];
        require(intent.createdAt > 0, "Intent does not exist");
        require(intent.status == IntentStatus.PENDING, "Intent not pending");

        // Verify caller is authorized
        require(
            siteRegistry.isSignerAuthorized(intent.siteId, msg.sender),
            "Not authorized to approve for this site"
        );

        // Check not already approved by this address
        require(
            approvals[intentId][msg.sender].approvedAt == 0,
            "Already approved"
        );

        approvals[intentId][msg.sender] = Approval({
            approver: msg.sender,
            signatureHash: signatureHash,
            comment: comment,
            approvedAt: block.timestamp
        });

        intentApprovers[intentId].push(msg.sender);
        intent.approvalCount++;

        // Check if fully approved
        if (intent.approvalCount >= intent.requiredApprovals) {
            intent.status = IntentStatus.APPROVED;
            intent.approvedAt = block.timestamp;
        }

        emit IntentApproved(
            intentId,
            msg.sender,
            intent.approvalCount,
            block.timestamp
        );
    }

    /**
     * @notice Reject a change intent
     * @param intentId The intent to reject
     * @param reason Reason for rejection
     */
    function rejectIntent(bytes32 intentId, string calldata reason) external {
        Intent storage intent = intents[intentId];
        require(intent.createdAt > 0, "Intent does not exist");
        require(intent.status == IntentStatus.PENDING, "Intent not pending");

        // Verify caller is authorized
        require(
            siteRegistry.isSignerAuthorized(intent.siteId, msg.sender),
            "Not authorized to reject for this site"
        );

        intent.status = IntentStatus.REJECTED;

        emit IntentRejected(intentId, msg.sender, reason, block.timestamp);
    }

    /**
     * @notice Mark an intent as deployed
     * @param intentId The intent that was deployed
     */
    function markDeployed(bytes32 intentId) external {
        Intent storage intent = intents[intentId];
        require(intent.createdAt > 0, "Intent does not exist");
        require(intent.status == IntentStatus.APPROVED, "Intent not approved");

        // Verify caller is authorized
        require(
            siteRegistry.isSignerAuthorized(intent.siteId, msg.sender),
            "Not authorized to mark deployed for this site"
        );

        intent.status = IntentStatus.DEPLOYED;
        intent.deployedAt = block.timestamp;
        intent.deployedBy = msg.sender;

        emit IntentDeployed(intentId, msg.sender, block.timestamp);
    }

    /**
     * @notice Mark an intent as rolled back
     * @param intentId The intent that was rolled back
     * @param reason Reason for rollback
     */
    function markRolledBack(bytes32 intentId, string calldata reason) external {
        Intent storage intent = intents[intentId];
        require(intent.createdAt > 0, "Intent does not exist");
        require(intent.status == IntentStatus.DEPLOYED, "Intent not deployed");

        // Verify caller is authorized
        require(
            siteRegistry.isSignerAuthorized(intent.siteId, msg.sender),
            "Not authorized to rollback for this site"
        );

        intent.status = IntentStatus.ROLLED_BACK;

        emit IntentRolledBack(intentId, msg.sender, reason, block.timestamp);
    }

    // View functions

    function getIntent(bytes32 intentId) external view returns (
        bytes32 siteId,
        bytes32 blueprintHash,
        bytes32 codeHash,
        bytes32 changePackageHash,
        uint256 requiredApprovals,
        uint256 approvalCount,
        IntentStatus status,
        uint256 createdAt,
        uint256 approvedAt,
        uint256 deployedAt,
        address createdBy,
        address deployedBy
    ) {
        Intent memory intent = intents[intentId];
        return (
            intent.siteId,
            intent.blueprintHash,
            intent.codeHash,
            intent.changePackageHash,
            intent.requiredApprovals,
            intent.approvalCount,
            intent.status,
            intent.createdAt,
            intent.approvedAt,
            intent.deployedAt,
            intent.createdBy,
            intent.deployedBy
        );
    }

    function getIntentCount() external view returns (uint256) {
        return intentIds.length;
    }

    function getSiteIntentCount(bytes32 siteId) external view returns (uint256) {
        return siteIntents[siteId].length;
    }

    function getIntentApproverCount(bytes32 intentId) external view returns (uint256) {
        return intentApprovers[intentId].length;
    }

    function getApproval(bytes32 intentId, address approver) external view returns (
        bytes32 signatureHash,
        string memory comment,
        uint256 approvedAt
    ) {
        Approval memory approval = approvals[intentId][approver];
        return (approval.signatureHash, approval.comment, approval.approvedAt);
    }

    function isFullyApproved(bytes32 intentId) external view returns (bool) {
        Intent memory intent = intents[intentId];
        return intent.approvalCount >= intent.requiredApprovals;
    }
}

contracts/EventAnchor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./SiteRegistry.sol";

/**
 * @title EventAnchor
 * @notice PRD Section 7.2: Event Anchor Contract
 * 
 * Stores:
 * - Merkle roots
 * - Metadata pointer (IPFS / content hash)
 * - Timestamp
 * 
 * Only authorized gateways or signers can anchor events.
 */
contract EventAnchor {

    SiteRegistry public immutable siteRegistry;

    struct Anchor {
        bytes32 siteId;
        bytes32 merkleRoot;
        bytes32 metadataHash;      // Hash of batch metadata
        string metadataUri;         // IPFS URI or other storage pointer
        uint256 eventCount;
        uint256 firstEventTimestamp;
        uint256 lastEventTimestamp;
        uint256 anchoredAt;
        address anchoredBy;
    }

    // Anchor ID => Anchor
    mapping(bytes32 => Anchor) public anchors;

    // Site ID => list of anchor IDs
    mapping(bytes32 => bytes32[]) public siteAnchors;

    // Track all anchor IDs
    bytes32[] public anchorIds;

    // Events
    event BatchAnchored(
        bytes32 indexed anchorId,
        bytes32 indexed siteId,
        bytes32 merkleRoot,
        uint256 eventCount,
        uint256 timestamp,
        address indexed anchoredBy
    );

    constructor(address _siteRegistry) {
        siteRegistry = SiteRegistry(_siteRegistry);
    }

    /**
     * @notice Anchor a batch of events
     * @param siteId The site this batch belongs to
     * @param merkleRoot The Merkle root of the event hashes
     * @param metadataHash Hash of the batch metadata
     * @param metadataUri URI pointing to full batch data (IPFS, etc.)
     * @param eventCount Number of events in the batch
     * @param firstEventTimestamp Timestamp of first event in batch
     * @param lastEventTimestamp Timestamp of last event in batch
     */
    function anchorBatch(
        bytes32 siteId,
        bytes32 merkleRoot,
        bytes32 metadataHash,
        string calldata metadataUri,
        uint256 eventCount,
        uint256 firstEventTimestamp,
        uint256 lastEventTimestamp
    ) external returns (bytes32 anchorId) {
        // Verify caller is authorized
        require(
            siteRegistry.isGatewayAuthorized(siteId, msg.sender) ||
            siteRegistry.isSignerAuthorized(siteId, msg.sender),
            "Not authorized to anchor for this site"
        );

        require(merkleRoot != bytes32(0), "Invalid Merkle root");
        require(eventCount > 0, "Event count must be positive");

        // Generate unique anchor ID
        anchorId = keccak256(abi.encodePacked(
            siteId,
            merkleRoot,
            block.timestamp,
            msg.sender
        ));

        require(anchors[anchorId].anchoredAt == 0, "Anchor already exists");

        anchors[anchorId] = Anchor({
            siteId: siteId,
            merkleRoot: merkleRoot,
            metadataHash: metadataHash,
            metadataUri: metadataUri,
            eventCount: eventCount,
            firstEventTimestamp: firstEventTimestamp,
            lastEventTimestamp: lastEventTimestamp,
            anchoredAt: block.timestamp,
            anchoredBy: msg.sender
        });

        anchorIds.push(anchorId);
        siteAnchors[siteId].push(anchorId);

        emit BatchAnchored(
            anchorId,
            siteId,
            merkleRoot,
            eventCount,
            block.timestamp,
            msg.sender
        );

        return anchorId;
    }

    /**
     * @notice Verify an event is included in an anchored batch
     * @param anchorId The anchor to verify against
     * @param eventHash The hash of the event
     * @param proof The Merkle proof
     * @param index The index of the event in the batch
     */
    function verifyEvent(
        bytes32 anchorId,
        bytes32 eventHash,
        bytes32[] calldata proof,
        uint256 index
    ) external view returns (bool) {
        Anchor memory anchor = anchors[anchorId];
        require(anchor.anchoredAt > 0, "Anchor does not exist");

        return _verifyMerkleProof(eventHash, proof, anchor.merkleRoot, index);
    }

    /**
     * @notice Internal Merkle proof verification
     */
    function _verifyMerkleProof(
        bytes32 leaf,
        bytes32[] calldata proof,
        bytes32 root,
        uint256 index
    ) internal pure returns (bool) {
        bytes32 computedHash = leaf;

        for (uint256 i = 0; i < proof.length; i++) {
            bytes32 proofElement = proof[i];

            if (index % 2 == 0) {
                computedHash = keccak256(abi.encodePacked(computedHash, proofElement));
            } else {
                computedHash = keccak256(abi.encodePacked(proofElement, computedHash));
            }

            index = index / 2;
        }

        return computedHash == root;
    }

    // View functions

    function getAnchor(bytes32 anchorId) external view returns (
        bytes32 siteId,
        bytes32 merkleRoot,
        bytes32 metadataHash,
        string memory metadataUri,
        uint256 eventCount,
        uint256 firstEventTimestamp,
        uint256 lastEventTimestamp,
        uint256 anchoredAt,
        address anchoredBy
    ) {
        Anchor memory anchor = anchors[anchorId];
        return (
            anchor.siteId,
            anchor.merkleRoot,
            anchor.metadataHash,
            anchor.metadataUri,
            anchor.eventCount,
            anchor.firstEventTimestamp,
            anchor.lastEventTimestamp,
            anchor.anchoredAt,
            anchor.anchoredBy
        );
    }

    function getAnchorCount() external view returns (uint256) {
        return anchorIds.length;
    }

    function getSiteAnchorCount(bytes32 siteId) external view returns (uint256) {
        return siteAnchors[siteId].length;
    }

    function getSiteAnchorAt(bytes32 siteId, uint256 index) external view returns (bytes32) {
        return siteAnchors[siteId][index];
    }
}

contracts/IndustrialRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IndustrialRegistry
 * @notice Smart contract for registering industrial sites, assets, and anchoring
 * event/maintenance records to the blockchain as an immutable audit ledger.
 * 
 * SAFETY: This contract does NOT control equipment directly. All real-time control
 * logic stays off-chain. This is purely for identity, registry, and audit.
 */
contract IndustrialRegistry {

    struct SiteRegistry {
        string name;
        string location;
        address owner;
        uint256 registeredAt;
        bool active;
    }

    struct AssetRegistry {
        string siteId;
        string assetType;
        string nameOrTag;
        bool critical;
        uint256 registeredAt;
    }

    struct EventAnchor {
        string assetId;
        string eventType;
        bytes32 payloadHash;
        uint256 timestamp;
        address recordedBy;
    }

    struct MaintenanceAnchor {
        string assetId;
        string workOrderId;
        string maintenanceType;
        uint256 performedAt;
        address performedBy;
    }

    mapping(string => SiteRegistry) public sites;
    mapping(string => AssetRegistry) public assets;

    string[] public siteIds;
    string[] public assetIds;

    event SiteRegistered(string indexed siteId, string name, address owner, uint256 timestamp);
    event AssetRegistered(string indexed assetId, string siteId, string assetType, uint256 timestamp);
    event EventAnchored(string indexed assetId, string eventType, bytes32 payloadHash, uint256 timestamp, address recordedBy);
    event MaintenanceAnchored(string indexed assetId, string workOrderId, string maintenanceType, uint256 timestamp, address performedBy);

    modifier onlyActiveSite(string memory siteId) {
        require(sites[siteId].active, "Site does not exist or is inactive");
        _;
    }

    /**
     * @notice Register a new industrial site
     */
    function registerSite(
        string memory siteId,
        string memory name,
        string memory location,
        address owner
    ) external {
        require(!sites[siteId].active, "Site already registered");
        require(bytes(siteId).length > 0, "Site ID cannot be empty");
        require(owner != address(0), "Owner cannot be zero address");

        sites[siteId] = SiteRegistry({
            name: name,
            location: location,
            owner: owner,
            registeredAt: block.timestamp,
            active: true
        });

        siteIds.push(siteId);
        emit SiteRegistered(siteId, name, owner, block.timestamp);
    }

    /**
     * @notice Register a new asset at a site
     */
    function registerAsset(
        string memory assetId,
        string memory siteId,
        string memory assetType,
        string memory nameOrTag,
        bool critical
    ) external onlyActiveSite(siteId) {
        require(bytes(assets[assetId].assetType).length == 0, "Asset already registered");
        require(bytes(assetId).length > 0, "Asset ID cannot be empty");

        assets[assetId] = AssetRegistry({
            siteId: siteId,
            assetType: assetType,
            nameOrTag: nameOrTag,
            critical: critical,
            registeredAt: block.timestamp
        });

        assetIds.push(assetId);
        emit AssetRegistered(assetId, siteId, assetType, block.timestamp);
    }

    /**
     * @notice Anchor an industrial event (breaker trip, setpoint change, etc.)
     * The full payload is stored off-chain, only the hash is anchored here.
     */
    function anchorEvent(
        string memory assetId,
        string memory eventType,
        bytes32 payloadHash
    ) external {
        require(bytes(assets[assetId].assetType).length > 0, "Asset not registered");
        require(payloadHash != bytes32(0), "Payload hash cannot be empty");

        emit EventAnchored(assetId, eventType, payloadHash, block.timestamp, msg.sender);
    }

    /**
     * @notice Anchor a maintenance record
     */
    function anchorMaintenance(
        string memory assetId,
        string memory workOrderId,
        string memory maintenanceType,
        uint256 performedAt
    ) external {
        require(bytes(assets[assetId].assetType).length > 0, "Asset not registered");
        require(bytes(workOrderId).length > 0, "Work order ID cannot be empty");

        emit MaintenanceAnchored(assetId, workOrderId, maintenanceType, performedAt, msg.sender);
    }

    /**
     * @notice Get site details
     */
    function getSite(string memory siteId) external view returns (SiteRegistry memory) {
        return sites[siteId];
    }

    /**
     * @notice Get asset details
     */
    function getAsset(string memory assetId) external view returns (AssetRegistry memory) {
        return assets[assetId];
    }

    /**
     * @notice Get total number of registered sites
     */
    function getSiteCount() external view returns (uint256) {
        return siteIds.length;
    }

    /**
     * @notice Get total number of registered assets
     */
    function getAssetCount() external view returns (uint256) {
        return assetIds.length;
    }
}

contracts/SiteRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title SiteRegistry
 * @notice PRD Section 7.1: Site Registry Contract
 * 
 * Stores:
 * - siteId â†’ owner
 * - authorized gateways
 * - authorized signers
 * 
 * Contracts are:
 * - Minimal
 * - Non-upgradeable
 * - Gas-efficient
 * - Auditable
 */
contract SiteRegistry {

    struct Site {
        address owner;
        bool active;
        uint256 registeredAt;
    }

    // siteId => Site
    mapping(bytes32 => Site) public sites;

    // siteId => gateway address => authorized
    mapping(bytes32 => mapping(address => bool)) public authorizedGateways;

    // siteId => signer address => authorized
    mapping(bytes32 => mapping(address => bool)) public authorizedSigners;

    // Track all site IDs
    bytes32[] public siteIds;

    // Events
    event SiteRegistered(bytes32 indexed siteId, address indexed owner, uint256 timestamp);
    event SiteOwnershipTransferred(bytes32 indexed siteId, address indexed previousOwner, address indexed newOwner);
    event SiteDeactivated(bytes32 indexed siteId, uint256 timestamp);
    event GatewayAuthorized(bytes32 indexed siteId, address indexed gateway, uint256 timestamp);
    event GatewayRevoked(bytes32 indexed siteId, address indexed gateway, uint256 timestamp);
    event SignerAuthorized(bytes32 indexed siteId, address indexed signer, uint256 timestamp);
    event SignerRevoked(bytes32 indexed siteId, address indexed signer, uint256 timestamp);

    // Modifiers
    modifier onlySiteOwner(bytes32 siteId) {
        require(sites[siteId].owner == msg.sender, "Not site owner");
        _;
    }

    modifier siteExists(bytes32 siteId) {
        require(sites[siteId].active, "Site does not exist or inactive");
        _;
    }

    /**
     * @notice Register a new site
     * @param siteId Unique identifier for the site (hash of off-chain ID)
     */
    function registerSite(bytes32 siteId) external {
        require(!sites[siteId].active, "Site already registered");
        require(siteId != bytes32(0), "Invalid site ID");

        sites[siteId] = Site({
            owner: msg.sender,
            active: true,
            registeredAt: block.timestamp
        });

        siteIds.push(siteId);

        // Owner is automatically an authorized signer
        authorizedSigners[siteId][msg.sender] = true;

        emit SiteRegistered(siteId, msg.sender, block.timestamp);
        emit SignerAuthorized(siteId, msg.sender, block.timestamp);
    }

    /**
     * @notice Transfer site ownership
     */
    function transferOwnership(bytes32 siteId, address newOwner) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        require(newOwner != address(0), "Invalid new owner");

        address previousOwner = sites[siteId].owner;
        sites[siteId].owner = newOwner;

        // New owner becomes authorized signer
        authorizedSigners[siteId][newOwner] = true;

        emit SiteOwnershipTransferred(siteId, previousOwner, newOwner);
        emit SignerAuthorized(siteId, newOwner, block.timestamp);
    }

    /**
     * @notice Deactivate a site
     */
    function deactivateSite(bytes32 siteId) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        sites[siteId].active = false;
        emit SiteDeactivated(siteId, block.timestamp);
    }

    /**
     * @notice Authorize a gateway for a site
     */
    function authorizeGateway(bytes32 siteId, address gateway) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        require(gateway != address(0), "Invalid gateway address");
        require(!authorizedGateways[siteId][gateway], "Gateway already authorized");

        authorizedGateways[siteId][gateway] = true;
        emit GatewayAuthorized(siteId, gateway, block.timestamp);
    }

    /**
     * @notice Revoke a gateway's authorization
     */
    function revokeGateway(bytes32 siteId, address gateway) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        require(authorizedGateways[siteId][gateway], "Gateway not authorized");

        authorizedGateways[siteId][gateway] = false;
        emit GatewayRevoked(siteId, gateway, block.timestamp);
    }

    /**
     * @notice Authorize a signer for a site
     */
    function authorizeSigner(bytes32 siteId, address signer) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        require(signer != address(0), "Invalid signer address");
        require(!authorizedSigners[siteId][signer], "Signer already authorized");

        authorizedSigners[siteId][signer] = true;
        emit SignerAuthorized(siteId, signer, block.timestamp);
    }

    /**
     * @notice Revoke a signer's authorization
     */
    function revokeSigner(bytes32 siteId, address signer) 
        external 
        onlySiteOwner(siteId) 
        siteExists(siteId) 
    {
        require(signer != sites[siteId].owner, "Cannot revoke owner");
        require(authorizedSigners[siteId][signer], "Signer not authorized");

        authorizedSigners[siteId][signer] = false;
        emit SignerRevoked(siteId, signer, block.timestamp);
    }

    // View functions

    function isGatewayAuthorized(bytes32 siteId, address gateway) external view returns (bool) {
        return authorizedGateways[siteId][gateway];
    }

    function isSignerAuthorized(bytes32 siteId, address signer) external view returns (bool) {
        return authorizedSigners[siteId][signer];
    }

    function getSiteCount() external view returns (uint256) {
        return siteIds.length;
    }

    function getSite(bytes32 siteId) external view returns (
        address owner,
        bool active,
        uint256 registeredAt
    ) {
        Site memory site = sites[siteId];
        return (site.owner, site.active, site.registeredAt);
    }
}

